function regTime(time) {
  var year = time.split('-')[0];
  var month = time.split('-')[1];
  var day = time.split('-')[2];
  var nowYear = new Date().getFullYear();
  var nowMonth = (new Date().getMonth() + 1 < 10 ? '0' : '') + (new Date().getMonth() + 1);
  var nowDay = (new Date().getUTCDate() < 10 ? '0' : '') + new Date().getDate();

  if (nowYear - year > 0) {
    if (nowMonth - month >= 0) {
      return "".concat(nowYear - year, " \u5E74\u524D");
    } else if (nowMonth - month < 0) {
      return "(".concat(12 - month + nowMonth, " \u500B\u6708\u524D");
    }

    ;
  } else if (nowYear - year === 0) {
    if (nowMonth - month > 0) {
      return "".concat(nowMonth - month, " \u500B\u6708\u524D");
    } else if (nowMonth - month === 0) {
      return "".concat(nowDay - day, " \u5929\u524D");
    }

    ;
  }

  ;
} //依照上架順序排序
